// Theme.js
const theme = {
    colors: {
      background: "#fae8dc",
      white: "#ffffff",
      text: "#000000",
      primary: "#a64040",
      border: "#edbcbc",
      shadow: "#d8baa4",
      highlight: "#ffd69d",
    },
    fonts: {
      body: "'Inder-Regular', Helvetica, sans-serif",
      header: "'Jersey 20-Regular', Helvetica, sans-serif",
    },
  };
  
  export default theme;
