import { createGlobalStyle } from "styled-components";

const GlobalStyles = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: ${({ theme }) => theme.fonts.body};
    background: linear-gradient(180deg, #ffffff 0%, #f5f5f5 100%);
    background-attachment: fixed; /* 배경 고정 */
    background-size: cover; /* 배경 크기를 화면에 맞춤 */
    background-position: center; /* 배경 가운데 정렬 */
    overflow-x: hidden; /* 가로 스크롤 방지 */
  }

  .desktop {
    position: relative;
    width: 100%;
    max-width: 1440px; /* 최대 크기 */
    margin: 0 auto; /* 화면 가운데 정렬 */
    z-index: 2; /* frame 위로 배치 */
    padding: 20px; /* 내부 컴포넌트 여백 */
  }

  .frame {
    position: fixed; /* 항상 화면에 고정 */
    width: 200%; /* 더 넓게 설정 */
    height: 1200px; /* 높이를 크게 설정 */
    top: -300px; /* 화면 위로 확장 */
    left: -50%; /* 화면 중심 기준 왼쪽으로 확장 */
    background: linear-gradient(180deg, #ffffff 0%, #f5f5f5 100%);
    border-radius: 0px 0px 800px 800px; /* 부드러운 곡선 */
    z-index: 1; /* desktop 아래 배치 */
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1); /* 부드러운 그림자 */
  }

  /* 반응형 처리 */
  @media (max-width: 768px) {
    .desktop {
      padding: 16px; /* 모바일에서 내부 여백 추가 */
    }
    .frame {
      height: 1000px; /* 모바일 높이 조정 */
      border-radius: 0px 0px 600px 600px; /* 곡선 크기 축소 */
    }
  }
`;

export default GlobalStyles;
