import { createGlobalStyle } from "styled-components";

const GlobalStyles = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: ${({ theme }) => theme.fonts.body};
    background: linear-gradient(180deg, #ffffff 0%, #f5f5f5 100%);
    background-size: cover; /* 배경을 화면 크기에 맞춤 */
    background-attachment: fixed; /* 배경 고정 */
    background-position: center; /* 배경을 가운데로 정렬 */
    overflow-x: hidden; /* 가로 스크롤 방지 */
  }

  .desktop {
    position: relative;
    width: 100%;
    max-width: 1440px; /* 최대 크기 */
    margin: 0 auto; /* 중앙 정렬 */
    background-color: #ffffff;
    border-radius: 0px 0px 400px 400px; /* 하단 모서리 둥글게 */
    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1); /* 그림자 */
    z-index: 1; /* frame 위로 배치 */
    overflow: hidden;
  }

  .frame {
    position: absolute;
    width: 200%; /* 배경을 크게 설정 */
    height: 1200px; /* 원하는 높이로 설정 */
    top: -200px; /* 화면을 위로 확장 */
    left: -50%; /* 가운데 정렬을 유지하면서 넓게 배치 */
    background: linear-gradient(180deg, #ffffff 0%, #f0f0f0 100%);
    border-radius: 0px 0px 800px 800px; /* 곡선을 더 부드럽게 설정 */
    z-index: 0; /* desktop 뒤로 배치 */
  }

  /* 반응형 처리 */
  @media (max-width: 768px) {
    .desktop {
      padding: 16px; /* 모바일에서 내부 여백 추가 */
      border-radius: 0; /* 둥근 모서리 제거 */
    }
  }
`;

export default GlobalStyles;
