// SeminarList.jsx
import React from "react";
import styled from "styled-components";
import SeminarItem from "./SeminarItem";

// Styled Components for SeminarList
const ListContainer = styled.div`
  position: absolute; /* 위치 지정 */
  width: 1013px;
  height: 1200px;
  top: 720px; /* 화면 위쪽에서의 거리 */
  left: 50%; /* 가로 방향 중앙 정렬 */
  transform: translateX(-50%); /* 정확한 중앙 정렬 */
  border-radius: 15px;
  overflow: hidden;
  overflow-y: auto; /* 콘텐츠가 넘칠 경우 세로 스크롤 추가 */
  border: 2px solid #edbdbd;
  box-shadow: 0px 6px 4px #d8baa4;

  /* 스크롤바 스타일 */
  &::-webkit-scrollbar {
    width: 10px; /* 스크롤바 너비 */
  }
  &::-webkit-scrollbar-thumb {
    background-color: #d8baa4; /* 스크롤바 색상 */
    border-radius: 5px; /* 스크롤바 모서리 둥글게 */
  }
  &::-webkit-scrollbar-track {
    background-color: #f9f9f9; /* 스크롤바 트랙 배경색 */
  }

`;

const HeaderRow = styled.div`
  display: flex;
  align-items: center;
  background-color: #fffbf6;
  border-bottom: 2px solid #edbcbc;
  height: 56px;
`;

const HeaderCell = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: "Inder-Regular", Helvetica, sans-serif;
  font-size: 20px;
  font-weight: 400;
  color: #000000;
  flex: ${({ flex }) => flex || 1};
`;

// Mock Data for SeminarList
const seminars = [
  {
    id: 192,
    title: "소프트웨어 재난 연구센터 11월 초청 세미나 안내",
    author: "이민지",
    category: "4차산업, 교양",
    date: "2024.11.11.",
  },  
  // 추가 세미나 데이터...
  {
    id: 193,
    title: "AI와 머신러닝의 현재와 미래",
    author: "김철수",
    category: "4차산업, 네트워킹",
    date: "2024.11.12.",
  },
  {
    id: 194,
    title: "웹 개발 트렌드 2024",
    author: "박영희",
    category: "IT, 창업",
    date: "2024.11.13.",
  },
  {
    id: 195,
    title: "글로벌 인턴십 설명회",
    author: "홍길동",
    category: "국제 교류",
    date: "2024.11.14.",
  },
  {
    id: 196,
    title: "UI/UX 디자인의 이해",
    author: "최수정",
    category: "멘토링, 교양",
    date: "2024.11.15.",
  },
  {
    id: 197,
    title: "2024 스타트업 성공 전략",
    author: "이지훈",
    category: "창업, 네트워킹",
    date: "2024.11.16.",
  },
  {
    id: 198,
    title: "데이터 분석을 통한 비즈니스 혁신",
    author: "장미정",
    category: "4차산업",
    date: "2024.11.17.",
  },
  {
    id: 199,
    title: "학부생 연구 발표대회",
    author: "백승민",
    category: "경진대회",
    date: "2024.11.18.",
  },
  {
    id: 200,
    title: "교환학생 프로그램 소개",
    author: "윤지우",
    category: "국제 교류",
    date: "2024.11.19.",
  },
  {
    id: 201,
    title: "블록체인 기술의 새로운 가능성",
    author: "신민호",
    category: "4차산업, IT",
    date: "2024.11.20.",
  },
];

function SeminarList() {
  return (
    <ListContainer>
      {/* Header */}
      <HeaderRow>
        <HeaderCell flex="0.5">번호</HeaderCell>
        <HeaderCell flex="3">제목</HeaderCell>
        <HeaderCell flex="1.5">작성자</HeaderCell>
        <HeaderCell flex="2">카테고리</HeaderCell>
        <HeaderCell flex="1.5">작성날짜</HeaderCell>
      </HeaderRow>
      {/* Seminar Items */}
      {seminars.map((seminar) => (
        <SeminarItem key={seminar.id} {...seminar} />
      ))}
    </ListContainer>
  );
}

export default SeminarList;
